// 动态计算输入框文字数量 by tonney .  http://www.duoguyu.com
;(function($){$.fn.textSum=function(){this.css({"position":"relative"});this.each(function(){var inputMaxlength=$(this).find("input,textarea").attr("maxlength");$(this).append('<div class="sumtips" style="position:absolute; width:70px; height:30px; line-height:30px; text-align:right; font-size:12px; color:#999; padding-right:15px; right:0; bottom:0;">'+inputMaxlength+"/"+inputMaxlength+"</div>");if(inputMaxlength==undefined||inputMaxlength=="undefined"){$(this).children("input,textarea").attr("maxlength","10");$(this).children(".sumtips").html($(this).find("input,textarea").attr("maxlength")+"/"+$(this).find("input,textarea").attr("maxlength"))}if($(this).children("input").length>0){var oH=$(this).height();$(this).children("input").css({"paddingRight":"70px"}).siblings(".sumtips").css({"height":oH+"px","line-height":oH+"px"})}if($(this).children("textarea").length>0){$(this).children("textarea").css({"paddingBottom":"30px"}).siblings(".sumtips").css({"bottom":"8px"})}});this.on("keyup","input,textarea",function(){var newSum=$(this).attr("maxlength")-$(this).val().length;$(this).siblings(".sumtips").html(newSum+"/"+$(this).attr("maxlength"))})}})(jQuery);